    return (
        <>
            <Label htmlFor='search'>
                Find contacts by name
                <Input
                type="text"
                name="search"
                value={value}
                    onChange={onChange}
                     placeholder={placeholder}
                />
            </Label>

        </>
    )
};


Filter.propTypes = {
    value: PropTypes.string.isRequired,
    onChange: PropTypes.func.isRequired,
};
